answer.code-block:
CREATE TABLE Person ( Person_ID NUMBER(7), Surname VARCHAR2(50) NOT NULL, Other_Names VARCHAR2(50) NOT NULL, Contact_Phone VARCHAR2(12), -- at least 12, maybe more Contact_Address VARCHAR2(200) NOT NULL, Username VARCHAR2(50) NOT NULL CONSTRAINT Person_Username_Unique UNIQUE, -- bonus marks! -- CONSTRAINT Person_PK PRIMARY KEY (Person_ID) );
CREATE TABLE Staff ( Staff_ID NUMBER(7), Email VARCHAR2(50) NOT NULL, Rank VARCHAR2(2) NOT NULL CONSTRAINT Staff Rank_Valid CHECK (Rank IN ('AL', 'L', 'SL', 'AP', 'P')), Salary NUMBER(7,2) NOT NULL CONSTRAINT Staff_Salary_Range CHECK (Salary >= 35450), -- CONSTRAINT Staff_PK PRIMARY KEY (Staff_ID), CONSTRAINT Staff_FK_to_Person FOREIGN KEY (Staff_ID) REFERENCES Person (Person_ID) );
CREATE TABLE Student ( Student_ID NUMBER(7), Email VARCHAR2(50) NOT NULL, Home_Phone VARCHAR2(12), Home_Address VARCHAR2(200) NOT NULL, International CHAR(1) DEFAULT 'F' NOT NULL CONSTRAINT Student_International_Valid CHECK (International IN ('T', 'F')), Supervisor_ID NUMBER(7), -- optional -- CONSTRAINT Student_PK PRIMARY KEY (Student_ID), CONSTRAINT Student_FK_to_Person FOREIGN KEY (Student_ID) REFERENCES Person (Person_ID), CONSTRAINT Student_FK_to_Staff FOREIGN KEY (Supervisor_ID) REFERENCES Staff (Staff_ID) );
